:root {
    --bg-dark: #0a0a0f;
    --primary: #f425af;
    --secondary: #00f2ff;
    --accent: #7000ff;
    --glass: rgba(255, 255, 255, 0.05);
    --glass-border: rgba(255, 255, 255, 0.1);
    --text: #ffffff;
    --text-dim: rgba(255, 255, 255, 0.6);
    --shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.8);
    --success: #00ff88;
    --error: #ff3366;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    -webkit-tap-highlight-color: transparent;
}

body {
    font-family: 'Outfit', sans-serif;
    background-color: var(--bg-dark);
    color: var(--text);
    overflow-x: hidden;
    height: 100vh;
    width: 100vw;
    background: radial-gradient(circle at 50% 50%, #1a1a2e 0%, #0a0a0f 100%);
    position: relative;
}

body::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: conic-gradient(from 180deg at 50% 50%, var(--primary) 0deg, transparent 60deg, var(--secondary) 120deg, transparent 180deg, var(--accent) 240deg, transparent 300deg, var(--primary) 360deg);
    animation: rotate 20s linear infinite;
    opacity: 0.1;
    z-index: -1;
}

@keyframes rotate {
    from {
        transform: rotate(0deg);
    }

    to {
        transform: rotate(360deg);
    }
}

/* Menu Bar */
.menu-bar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 70px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 40px;
    background: var(--glass);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--glass-border);
    z-index: 1000;
}

.nav-btn {
    background: transparent;
    border: 2px solid var(--glass-border);
    color: var(--text);
    padding: 15px 35px;
    border-radius: 100px;
    font-size: 1.4rem;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}


.nav-btn:hover {
    background: var(--primary);
    border-color: var(--primary);
}

.team-scores {
    display: flex;
    gap: 15px;
}

.score-chip {
    padding: 12px 25px;
    border-radius: 100px;
    background: var(--glass);
    font-weight: 800;
    font-size: 1.2rem;
    border: 2px solid var(--glass-border);
    color: var(--secondary);
}


/* App Container */
#app-container {
    height: 100vh;
    padding-top: 70px;
    display: flex;
    flex-direction: column;
}

.screen {
    flex: 1;
    display: none;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 40px;
    animation: fadeIn 0.6s ease-out;
}

.screen.active {
    display: flex;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Home Screen */
.glow-text {
    font-size: 5rem;
    font-weight: 800;
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: 0 0 30px rgba(244, 37, 175, 0.4);
    margin-bottom: 20px;
}

.glow-text.mini {
    font-size: 3.5rem;
}

.welcome-msg {
    font-size: 1.6rem;
    color: var(--secondary);
    text-shadow: 0 0 10px rgba(0, 242, 254, 0.5);
    font-weight: 300;
    margin-bottom: 50px;
    letter-spacing: 1px;
}

.main-btn {
    padding: 20px 45px;
    border-radius: 100px;
    font-size: 1.3rem;
    font-weight: 800;
    border: none;
    cursor: pointer;
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    text-transform: uppercase;
    letter-spacing: 1px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.main-btn.primary {
    background: linear-gradient(45deg, var(--primary), var(--accent));
    color: white;
}

.main-btn:hover {
    transform: scale(1.05) translateY(-5px);
    box-shadow: 0 20px 40px rgba(244, 37, 175, 0.4);
}

/* Themes Screen */
.section-title {
    font-size: 2.5rem;
    margin-bottom: 40px;
}

.themes-grid {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 30px;
    width: 100%;
    max-width: 1200px;
    overflow-y: auto;
    padding-bottom: 50px;
}

.theme-card {
    width: calc(33.33% - 20px);
    min-width: 300px;
    cursor: pointer;
    transition: all 0.5s cubic-bezier(0.23, 1, 0.32, 1);
}

.poster-container {
    position: relative;
    width: 100%;
    aspect-ratio: 16/10;
    border-radius: 40px;
    overflow: hidden;
    background: var(--glass);
    border: 1px solid var(--glass-border);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.5);
}

.poster-placeholder {
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    transition: transform 0.8s ease;
}

.poster-placeholder.theme-80s {
    background: linear-gradient(135deg, #ff00cc, #3333ff);
}


.poster-placeholder.poprock {
    background: linear-gradient(135deg, #e63946, #48cae4);
}

.poster-placeholder.disney {
    background: linear-gradient(135deg, #ffb703, #fb8500);
}

.poster-placeholder.rapfr {
    background: linear-gradient(135deg, #2b9348, #80b918);
}

.poster-placeholder.rapus {
    background: linear-gradient(135deg, #7209b7, #f72585);
}

.poster-placeholder.cartoons {
    background: linear-gradient(135deg, #ff9966, #ff5e62);
}

.poster-placeholder.movies {
    background: linear-gradient(135deg, #232526, #414345);
}

.poster-placeholder.series {
    background: linear-gradient(135deg, #6a11cb, #2575fc);
}


.poster-placeholder.random-grad {
    background: linear-gradient(45deg, #f425af, #00f2ff, #7000ff, #f425af);
    background-size: 400% 400%;
    animation: gradientFlow 5s ease infinite;
}

@keyframes gradientFlow {
    0% {
        background-position: 0% 50%;
    }

    50% {
        background-position: 100% 50%;
    }

    100% {
        background-position: 0% 50%;
    }
}


.poster-overlay {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 20px;
    background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
    display: flex;
    align-items: flex-end;
}

.poster-overlay span {
    font-size: 1.8rem;
    font-weight: 700;
}

.theme-card:hover {
    transform: scale(1.05);
}

.theme-card:hover .poster-placeholder {
    transform: scale(1.1);
}

/* Game Screen */
.game-ui {
    width: 100%;
    max-width: 1000px;
    display: flex;
    flex-direction: column;
    gap: 40px;
    align-items: center;
}

.now-playing {
    width: 100%;
    height: 350px;
    /* Slightly increased height */
    background: var(--glass);
    border-radius: 40px;
    border: 1px solid var(--glass-border);
    position: relative;
    display: flex;
    flex-direction: column;
    /* Stacked */
    align-items: center;
    justify-content: center;
    gap: 30px;
    /* Space between logo and timer */
}


.timer {
    font-size: 6rem;
    font-weight: 800;
    color: var(--secondary);
    text-shadow: 0 0 20px var(--secondary);
    line-height: 1;
    /* Tighten up */
}


.visualizer {
    display: flex;
    gap: 8px;
    height: 120px;
    align-items: flex-end;
}

.bar {
    width: 12px;
    background: linear-gradient(to top, var(--primary), var(--secondary));
    border-radius: 100px;
    animation: wave 1.2s ease-in-out infinite;
}

.bar:nth-child(1) {
    animation-delay: 0.1s;
    height: 40%;
}

.bar:nth-child(2) {
    animation-delay: 0.3s;
    height: 60%;
}

.bar:nth-child(3) {
    animation-delay: 0.2s;
    height: 80%;
}

.bar:nth-child(4) {
    animation-delay: 0.4s;
    height: 50%;
}

.bar:nth-child(5) {
    animation-delay: 0.15s;
    height: 70%;
}

@keyframes wave {

    0%,
    100% {
        transform: scaleY(1);
        opacity: 0.5;
    }

    50% {
        transform: scaleY(1.8);
        opacity: 1;
        filter: drop-shadow(0 0 10px var(--secondary));
    }
}

.reveal-card {
    position: fixed;
    inset: 0;
    background: #0a0b1e;
    /* Pure background to hide everything */
    z-index: 10000;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 0;
    margin: 0;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: scale(1.1);
    }

    to {
        opacity: 1;
        transform: scale(1);
    }
}

.hidden {
    display: none !important;
}

.reveal-content-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 40px;
    width: 100%;
    max-width: 1000px;
}

.reveal-content {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    gap: 50px;
}

.validation-controls {
    display: flex;
    gap: 40px;
    margin-top: 20px;
}

.val-btn {
    padding: 25px 60px;
    border-radius: 100px;
    font-size: 1.5rem;
    font-weight: 900;
    cursor: pointer;
    text-transform: uppercase;
    transition: all 0.3s;
    color: white;
    border: none;
}


.val-btn:active {
    transform: scale(0.95);
}

.val-btn.correct {
    background: linear-gradient(135deg, #00f2fe 0%, #4facfe 100%);
    box-shadow: 0 0 50px rgba(0, 242, 254, 0.5);
    border: none;
}

.val-btn.wrong {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    box-shadow: 0 0 50px rgba(245, 87, 108, 0.5);
    border: none;
}

.val-btn:hover {
    transform: translateY(-8px);
    border-color: rgba(255, 255, 255, 0.3);
}

.mini-cover {
    width: 320px;
    height: 320px;
    border-radius: 60px;
    background: var(--glass);
    box-shadow: 0 40px 80px rgba(0, 0, 0, 0.8);
    border: 3px solid var(--glass-border);
}

.reveal-info h3 {
    font-size: 4rem;
    color: white;
    margin-bottom: 10px;
}

.reveal-info p {
    font-size: 2.5rem;
    color: var(--secondary);
    font-weight: 700;
}

.main-btn.next {
    margin-top: 40px;
    background: var(--secondary);
    color: var(--bg-dark);
    box-shadow: 0 0 30px rgba(0, 242, 254, 0.4);
    animation: pulseNext 2s infinite;
}

@keyframes pulseNext {
    0% {
        transform: scale(1);
        box-shadow: 0 0 20px rgba(0, 242, 254, 0.4);
    }

    50% {
        transform: scale(1.05);
        box-shadow: 0 0 40px rgba(0, 242, 254, 0.6);
    }

    100% {
        transform: scale(1);
        box-shadow: 0 0 20px rgba(0, 242, 254, 0.4);
    }
}


.bravo-container {
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
}


.hints-container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 20px;
    width: 100%;
}

.hint-btn {
    padding: 20px;
    border-radius: 25px;
    border: 1px solid var(--glass-border);
    background: var(--glass);
    color: var(--text);
    font-size: 1.2rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s;
}

.hint-btn:hover {
    background: var(--secondary);
    color: var(--bg-dark);
}

.teams-action {
    display: flex;
    gap: 30px;
    width: 100%;
}

.team-btn {
    flex: 1;
    padding: 30px;
    border-radius: 30px;
    font-size: 1.8rem;
    font-weight: 800;
    border: none;
    cursor: pointer;
    transition: all 0.3s;
    background: rgba(255, 255, 255, 0.1);
    color: white;
}

.team-btn[data-team="1"]:hover {
    background: #3498db;
}

.team-btn[data-team="2"]:hover {
    background: #e74c3c;
}

.main-btn.next {
    background: var(--secondary);
    color: var(--bg-dark);
}

/* Modals */
.modal {
    position: fixed;
    inset: 0;
    z-index: 2000;
    display: none;
    align-items: center;
    justify-content: center;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(5px);
}

.modal.active {
    display: flex;
}

.modal-content {
    padding: 50px;
    border-radius: 50px;
    text-align: center;
    width: 90%;
    max-width: 500px;
    display: flex;
    flex-direction: column;
    gap: 20px;
}

.glass {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid var(--glass-border);
    backdrop-filter: blur(15px);
    will-change: transform, opacity;
}

input {
    padding: 15px 25px;
    border-radius: 100px;
    border: 1px solid var(--glass-border);
    background: rgba(0, 0, 0, 0.3);
    color: white;
    font-size: 1.2rem;
    text-align: center;
}

/* Results Screen Stitch 2026 */
#screen-results {
    background: radial-gradient(circle at center, #1a1b3b 0%, #0a0b1e 100%);
}

.results-container {
    width: 90%;
    max-width: 1000px;
    padding: 80px 40px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 60px;
    position: relative;
    overflow: hidden;
    animation: slideUp 0.8s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.trophy-glow {
    position: absolute;
    top: -100px;
    width: 400px;
    height: 400px;
    background: radial-gradient(circle, var(--primary) 0%, transparent 70%);
    opacity: 0.2;
    filter: blur(60px);
    animation: pulseGlow 4s infinite alternate;
}

@keyframes pulseGlow {
    from {
        opacity: 0.1;
        transform: scale(1);
    }

    to {
        opacity: 0.3;
        transform: scale(1.5);
    }
}

.podium-header {
    text-align: center;
}

.winner-label {
    font-size: 1.5rem;
    font-weight: 900;
    color: var(--secondary);
    letter-spacing: 10px;
    text-shadow: 0 0 20px var(--secondary);
}

#winner-name {
    font-size: 7rem;
    font-weight: 900;
    background: linear-gradient(to bottom, #fff, #888);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;

    margin-top: 10px;
    letter-spacing: -2px;
}

.final-scores {
    display: flex;
    align-items: center;
    gap: 50px;
    width: 100%;
    justify-content: center;
}

.final-score-card {
    background: rgba(255, 255, 255, 0.05);
    padding: 40px;
    border-radius: 40px;
    border: 1px solid var(--glass-border);
    flex: 1;
    max-width: 300px;
    text-align: center;
    transition: transform 0.4s;
}

.final-score-card:hover {
    transform: translateY(-10px);
}

.final-score-card span {
    display: block;
    font-size: 1.5rem;
    color: var(--text-dim);
    margin-bottom: 20px;
}

.final-score-card h2 {
    font-size: 5rem;
    font-weight: 900;
    color: white;
}

.final-score-divider {
    width: 2px;
    height: 100px;
    background: linear-gradient(to bottom, transparent, var(--glass-border), transparent);
}

.results-actions {
    display: flex;
    gap: 30px;
    width: 100%;
    justify-content: center;
    margin-top: 20px;
}



@keyframes scaleBravo {
    from {
        transform: scale(1);
        filter: brightness(1);
    }

    to {
        transform: scale(1.05);
        filter: brightness(1.2);
    }
}

.feedback-text {
    padding: 15px 40px;
    border-radius: 100px;
    font-size: 3rem;
    font-weight: 900;
    text-transform: uppercase;
    letter-spacing: 5px;
    display: inline-block;
    backdrop-filter: blur(20px);
    border: 2px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.5);
    animation: feedbackPop 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards, scaleBravo 1.5s infinite alternate;
}

.feedback-bravo {
    background: linear-gradient(135deg, rgba(0, 242, 254, 0.2), rgba(79, 172, 254, 0.2));
    color: #4facfe;
    border-color: #4facfe;
    text-shadow: 0 0 20px #4facfe;
    box-shadow: 0 10px 40px rgba(79, 172, 254, 0.4);
}

.feedback-dommage {
    background: linear-gradient(135deg, rgba(240, 147, 251, 0.2), rgba(245, 87, 108, 0.2));
    color: #f5576c;
    border-color: #f5576c;
    text-shadow: 0 0 20px #f5576c;
    box-shadow: 0 10px 40px rgba(245, 87, 108, 0.4);
}

@keyframes feedbackPop {
    0% {
        transform: scale(0.5) translateY(50px);
        opacity: 0;
    }

    100% {
        transform: scale(1) translateY(0);
        opacity: 1;
    }
}

/* --- ROLE SELECTOR --- */
.role-selector {
    display: flex;
    gap: 30px;
    margin-top: 50px;
}

.role-card {
    background: var(--glass);
    border: 1px solid var(--glass-border);
    border-radius: 40px;
    padding: 40px;
    width: 280px;
    cursor: pointer;
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    color: white;
}

.role-card:hover {
    transform: translateY(-15px);
    background: rgba(255, 255, 255, 0.1);
    border-color: var(--secondary);
    box-shadow: 0 20px 50px rgba(0, 242, 254, 0.2);
}

.role-icon {
    font-size: 4rem;
    margin-bottom: 20px;
}

.role-card h3 {
    font-size: 1.8rem;
    margin-bottom: 10px;
}

.role-card p {
    color: var(--text-dim);
}

/* --- ROOM CODE TAG --- */
.room-code-tag {
    background: var(--accent);
    padding: 10px 25px;
    border-radius: 100px;
    font-weight: 900;
    color: white;
    box-shadow: 0 0 20px var(--accent);
    margin-right: 20px;
}

/* --- GAME SETUP OPTIONS --- */
.game-setup-options {
    padding: 30px;
    border-radius: 30px;
    margin-bottom: 40px;
    width: 100%;
    max-width: 600px;
}

.setup-group label {
    display: block;
    margin-bottom: 20px;
    font-weight: 700;
    font-size: 1.2rem;
    color: var(--secondary);
}

.mode-btns {
    display: flex;
    gap: 15px;
    justify-content: center;
}

.mode-btn {
    flex: 1;
    padding: 15px;
    border-radius: 100px;
    border: 2px solid var(--glass-border);
    background: transparent;
    color: white;
    font-weight: 800;
    cursor: pointer;
    transition: all 0.3s;
}

.mode-btn.active {
    background: var(--secondary);
    color: var(--bg-dark);
    border-color: var(--secondary);
    box-shadow: 0 0 20px var(--secondary);
}

/* --- PLAYER UI --- */
.player-lobby {
    display: flex;
    flex-direction: column;
    gap: 25px;
    width: 100%;
    max-width: 400px;
}

.room-input {
    font-size: 2.5rem;
    letter-spacing: 10px;
    text-transform: uppercase;
    font-weight: 900;
}

.team-select {
    padding: 20px;
    background: var(--glass);
    border: 1px solid var(--glass-border);
    color: white;
    border-radius: 20px;
    font-size: 1.2rem;
    font-weight: 700;
}

.player-game {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.huge-buzz-btn {
    width: min(280px, 60vw);
    height: min(280px, 60vw);
    border-radius: 50%;
    background: radial-gradient(circle at 30% 30%, #ff4d4d, #b30000);
    border: 10px solid rgba(255, 255, 255, 0.2);
    color: white;
    font-size: min(3rem, 10vw);
    font-weight: 900;
    cursor: pointer;
    box-shadow: 0 20px 0 #660000, 0 30px 60px rgba(255, 0, 0, 0.5);
    transition: all 0.1s;
    display: flex;
    align-items: center;
    justify-content: center;
