    }

    50% {
        opacity: 1;
    }
}

.player-status-indicator {
    padding: 15px 30px;
    border-radius: 100px;
    font-weight: 800;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: 40px;
