/* Scrollbar Style 2026 */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb {
    background: var(--glass-border);
    border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--primary);
}

/* Spicy Mechanics Layout & Stitch 2026 Aesthetics */
.game-header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 15px;
    margin-bottom: 20px;
}

.modifier-badge {
    padding: 8px 25px;
    border-radius: 50px;
    font-size: 1.1rem;
    font-weight: 900;
    text-transform: uppercase;
    letter-spacing: 2px;
    background: var(--glass);
    border: 1px solid var(--glass-border);
    color: var(--secondary);
    box-shadow: 0 0 20px rgba(0, 242, 254, 0.2);
    animation: slideDown 0.5s ease;
}

.modifier-double {
    background: linear-gradient(90deg, #ff00cc, #3333ff) !important;
    color: white !important;
    border: none;
}

.modifier-mystery {
    background: #ffeb3b !important;
    color: #000 !important;
    border: none;
}

.modifier-bonus1 {
    background: linear-gradient(90deg, #00b09b, #96c93d) !important;
    color: white !important;
    border: none;
    box-shadow: 0 0 20px rgba(0, 176, 155, 0.4);
}

.modifier-bonus3 {
    background: linear-gradient(90deg, #4facfe, #00f2fe) !important;
    color: white !important;
    border: none;
    box-shadow: 0 0 30px rgba(79, 172, 254, 0.6);
    animation: pulseGlow 1.5s infinite alternate;
}

.modifier-fast {
    background: #ff4757 !important;
    color: white !important;
    border: none;
    box-shadow: 0 0 20px rgba(255, 71, 87, 0.5);
    animation: shake 0.5s infinite;
}

.modifier-steal {
    background: #2f3542 !important;
    color: #ffa502 !important;
    border: 2px solid #ffa502;
    box-shadow: 0 0 20px rgba(255, 165, 2, 0.3);
}

@keyframes shake {

    0%,
    100% {
        transform: translateX(0);
    }

    25% {
        transform: translateX(-2px);
    }

    75% {
        transform: translateX(2px);
    }
}


.teams-action {
    display: flex;
    gap: 40px;
    width: 100%;
    max-width: 900px;
    justify-content: center;
    margin-top: 30px;
}

.team-block {
    display: flex;
    flex-direction: column;
    gap: 15px;
    flex: 1;
}

.joker-btn {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--glass-border);
    color: #FFD700;
    padding: 15px;
    border-radius: 20px;
    font-size: 0.95rem;
    font-weight: 900;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    text-transform: uppercase;
    backdrop-filter: blur(10px);
}

.joker-btn.active {
    background: linear-gradient(135deg, #FFD700, #FFA500);
    color: #000;
    border: none;
    box-shadow: 0 0 30px rgba(255, 215, 0, 0.6);
    transform: scale(1.1);
}

.joker-btn.used {
    background: rgba(255, 255, 255, 0.05);
    color: rgba(255, 255, 255, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.05);
    cursor: default;
    pointer-events: none;
    filter: grayscale(1);
    box-shadow: none;
    text-decoration: line-through;
}

.speed-bonus-text {
    font-size: 1.5rem;
    color: #ffeb3b;
    font-weight: 900;
    text-shadow: 0 0 15px #ffeb3b;
    margin-bottom: 20px;
    animation: feedbackPop 0.5s forwards;
}

@keyframes slideDown {
    from {
        transform: translateY(-30px);
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}


/* Bonus Particle System */
.particle {
    position: fixed;
    top: -50px;
    z-index: 15000;
    pointer-events: none;
    animation: fall linear forwards;
}

.heart-particle {
    font-size: 2rem;
    color: #ff4d4d;
    filter: drop-shadow(0 0 10px rgba(255, 77, 77, 0.8));
}

.bonus-label {
    font-size: 2.5rem;
    font-weight: 900;
    color: #ffeb3b;
    text-shadow: 0 0 20px rgba(255, 235, 59, 1);
    font-family: 'Spline Sans', sans-serif;
    white-space: nowrap;
}

.penalty-label {
    font-size: 2.5rem;
    font-weight: 900;
    color: #ff4d4d;
    /* Red for penalty */
    text-shadow: 0 0 20px rgba(255, 77, 77, 1);
    font-family: 'Spline Sans', sans-serif;
    white-space: nowrap;
}


@keyframes fall {
    0% {
        transform: translate3d(0, 0, 0) rotate(0deg);
        -webkit-transform: translate3d(0, 0, 0) rotate(0deg);
        opacity: 1;
    }

    100% {
        transform: translate3d(var(--drift), 120vh, 0) rotate(var(--rot));
        -webkit-transform: translate3d(var(--drift), 120vh, 0) rotate(var(--rot));
        opacity: 0;
    }
}

/* Team Selection & Count */
.team-count-selector {
    margin-bottom: 20px;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.count-btns {
    display: flex;
    justify-content: center;
    gap: 25px;
    margin: 15px 0;
}

.count-btn {
    width: 90px;
    height: 75px;
    border-radius: 20px;
    border: 2px solid var(--glass-border);
    background: var(--glass);
    color: white;
    font-size: 2.2rem;
    font-weight: 900;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    display: flex;
    align-items: center;
    justify-content: center;
}

.count-btn.active {
    background: var(--secondary);
    color: var(--bg-dark);
    border-color: var(--secondary);
    box-shadow: 0 0 30px var(--secondary), inset 0 0 15px rgba(255, 255, 255, 0.5);
    transform: scale(1.1);
}


.team-inputs {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

/* Results Podium */
.winner-message {
    font-size: 1.4rem;
    color: var(--secondary);
    margin-top: 15px;
    font-weight: 600;
    text-shadow: 0 0 10px rgba(0, 242, 254, 0.3);
}

.final-podium {
    display: flex;
    align-items: flex-end;
    justify-content: center;
    gap: 20px;
