}

.rank-2 .podium-bar {
    height: 80%;
    border-color: #C0C0C0;
}

.rank-3 .podium-bar {
    height: 65%;
    border-color: #CD7F32;
}

.rank-4 .podium-bar {
    height: 50%;
}

.podium-name {
    font-weight: 800;
    font-size: 1.1rem;
    margin-bottom: 5px;
    text-align: center;
}

.podium-pts {
    font-size: 2rem;
    font-weight: 900;
}

.podium-comfort {
    text-align: center;
    font-style: italic;
    color: var(--text-dim);
    font-size: 1.2rem;
    max-width: 600px;
}

@keyframes slideUpPodium {
    from {
        transform: translateY(100px);
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

/* Bonus & Streaks Animations */
@keyframes talentScale {
    0% {
        transform: scale(0.5);
        opacity: 0;
    }

    50% {
        transform: scale(1.2);
