}

/* La Roue du Destin - Slot Machine 2026 */
.modal-wheel {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 2000;
    display: flex;
    align-items: center;
    justify-content: center;
}

.wheel-overlay {
    position: absolute;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.85);
    backdrop-filter: blur(15px);
    animation: fadeIn 0.4s ease;
}

.slot-machine {
    position: relative;
    width: 90vw;
    max-width: 600px;
    background: linear-gradient(145deg, #1a1a2e, #16213e);
    border: 3px solid var(--secondary);
    border-radius: 40px;
    padding: 60px 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    box-shadow: 0 0 50px rgba(0, 242, 254, 0.3), inset 0 0 30px rgba(0, 0, 0, 0.5);
    animation: slideUp 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.slot-title {
    font-size: 2.2rem;
    font-weight: 900;
    color: white;
    margin-bottom: 40px;
    text-shadow: 0 0 15px var(--secondary);
    text-align: center;
}

.slot-window {
    width: 100%;
    height: 120px;
    background: #000;
    border-radius: 20px;
    overflow: hidden;
    position: relative;
    border: 4px solid rgba(255, 255, 255, 0.1);
    box-shadow: inset 0 0 20px rgba(255, 255, 255, 0.05);
}

.slot-reel {
    display: flex;
    flex-direction: column;
    align-items: center;
    transition: transform 3s cubic-bezier(0.45, 0.05, 0.55, 0.95);
}

.slot-item {
    height: 120px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2.5rem;
    font-weight: 900;
    color: white;
    text-transform: uppercase;
    flex-shrink: 0;
}

.slot-light {
    position: absolute;
    width: 20px;
    height: 20px;
    background: var(--secondary);
    border-radius: 50%;
    top: 50%;
    transform: translateY(-50%);
    box-shadow: 0 0 20px var(--secondary);
    animation: pulseGlow 1s infinite alternate;
}

.slot-light.left {
    left: 20px;
}

.slot-light.right {
    right: 20px;
}

/* Styles sp√©cifiques par modificateur */
.slot-item.double {
    color: #ff00cc;
    text-shadow: 0 0 20px #ff00cc;
}

.slot-item.mystery {
    color: #ffeb3b;
    text-shadow: 0 0 20px #ffeb3b;
}

.slot-item.bonus1 {
    color: #00b09b;
    text-shadow: 0 0 20px #00b09b;
}

.slot-item.bonus3 {
    color: #4facfe;
    text-shadow: 0 0 20px #4facfe;
}

.slot-item.fast {
    color: #ff4757;
